package selenium;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       String i ="Orange";
       switch(i){
       case "Orange":
    	   System.out.println("value is 1");
    	   System.out.println("Value 1 he");
    	   break;
       case "Mango":
    	   System.out.println("value is 2");
    	   System.out.println("Value 2 he");
    	   break;   
    	   default:
    		   System.out.println("value i also don't know");
    		   break;
       }
	}

}
